import argparse
from plinth import action_utils
def parse_arguments(): """Return parsed command line arguments as dictionary.""" parser = argparse.ArgumentParser() subparsers = parser.add_subparsers(dest='subcommand', help='Sub command')
subparsers.add_parser('enable', help='Enable Tiny Tiny RSS') subparsers.add_parser('disable', help='Disable Tiny Tiny RSS')
return parser.parse_args()
def subcommand_enable(_): """Enable web configuration and reload.""" action_utils.webserver_enable('50-tt-rss')
def subcommand_disable(_): """Disable web configuration and reload.""" action_utils.webserver_disable('50-tt-rss')
def main(): """Parse arguments and perform all duties.""" arguments = parse_arguments()
subcommand = arguments.subcommand.replace('-', '_') subcommand_method = globals()['subcommand_' + subcommand] subcommand_method(arguments)
if __name__ == '__main__': main()

